var topic;

switch (msg.payload[8]) {
    case 0x23: { // AC Status
        var ACnumber = (msg.payload[16] & 0x0F);
        var root = 'home/airtouch2p/'+ACnumber;
        var payload;
        var action;

        // AC power state
        topic = '/power';
        switch (msg.payload[16] & 0xF0) { // AC Power State
            case 0: // 0000xxxx
                payload = 'off';
                action = 'off'
                break;
            case 16: // 0001xxxx
                payload = 'on';
                break;
            case 32: // 0010xxxx
                payload = 'away off';
                break;
            case 48: // 0011xxxx
                payload = 'away on';
                break;
            case 80: // 0101xxxx
                payload = 'sleep';
                break;
            default:
                payload = '';
                break;
        }
        node.send({ payload: payload, topic: root+topic }, false);

        // AC mode
        topic = '/mode/state';
        payload = false;
        switch (msg.payload[17] & 0xF0) {
            case 0: // 0000xxxx
                payload = 'auto';
                break;
            case 16: // 0001xxxx
                payload = "heat";
                break;
            case 32: // 0010xxxx
                payload = "dry";
                break;
            case 48: // 0011xxxx
                payload = "fan_only";
                break;
            case 64: // 0100xxxx
                payload = "cool";
                break
            case 128: // 1000xxxx
                payload = "auto_heat";
                break
            case 144: // 1001xxxx
                payload = "auto_cool";
                break
            default:
                payload = '';
                break;
        }
        node.send({ payload: payload, topic: root + topic }, false);

        // HA Action
        topic = '/action/state';
        if (action != 'off') {
            switch (payload) {
                case 'auto':
                    payload = 'idle'
                    break;
                case 'heat':
                case 'cool':
                case 'dry':
                    payload = payload + 'ing';
                    break;
                case 'fan_only':
                    payload = 'fan'
                    break;
                default:
                    payload = 'off'
                    break;
            }
        } else {
            payload = 'off';
        }
        node.send({ payload: payload, topic: root + topic }, false);

        // AC fan speed
        topic = '/fan_speed/state';
        payload = false;
        switch (msg.payload[17] & 0x0F) {
            case 0: // xxxx0000
                payload = "auto";
                break;
            case 1: // xxxx0001
                payload = "quiet";
                break;
            case 2: // xxxx0010
                payload = "low";
                break;
            case 3: // xxxx0011
                payload = "medium";
                break;
            case 4: // xxxx0100
                payload = "high";
                break;
            case 5: // xxxx0101
                payload = "powerful";
                break;
            case 6: // xxxx0110
                payload = "turbo";
                break;
            default:
                payload = '';
                break;
        }
        node.send({ payload: payload, topic: root + topic }, false);

        // Setpoint
        // 0-250: Setpoint = (VALUE + 100)/10. 
        topic = '/setpoint/state';
        payload = (msg.payload[18] + 100) / 10;
        node.send({ payload: payload, topic: root + topic }, false);

        // Temperature
        // 0 - 2000: Temperature = (VALUE â€“ 500) /10
        topic = '/temperature/state';
//        payload = ((parseInt(msg.payload[20].toString(16) + msg.payload[21].toString(16), 16)) - 500) / 10
        payload = ((((msg.payload[20] & 0xff) << 8) | (msg.payload[21] & 0xff)) - 500) / 10;
        node.send({ payload: payload, topic: root + topic }, false);

        break;
    }
    case 0x21: { // Group Status

        break;
    }
}

node.done();
return;

var CRCMaster = {
    StringToCheck: "",
    CleanedString: "",
    CRCTableDNP: [],
    init: function() {
        this.CRCDNPInit();
    },
    CleanString: function(inputType) {
        if (inputType == "ASCII") {
            this.CleanedString = this.StringToCheck;
        } else {
            if (this.StringToCheck.match(/^[0-9A-F \t]+$/gi) !== null) {
                this.CleanedString = this._hexStringToString(this.StringToCheck.toUpperCase().replace(/[\t ]/g, ''));
            } else {
                console.error("String doesn't seem to be a valid Hex input.");
                return false;
            }
        }
        return true;
    },
    CRCDNPInit: function() {
        var i, j, crc, c;
        for (i = 0; i < 256; i++) {
            crc = 0;
            c = i;
            for (j = 0; j < 8; j++) {
                if ((crc ^ c) & 0x0001) crc = (crc >> 1) ^ 0xA6BC;
                else crc = crc >> 1;
                c = c >> 1;
            }
            this.CRCTableDNP[i] = crc;
        }
    },
    CRC16Modbus: function() {
        var crc = 0xFFFF;
        var str = this.CleanedString;
        for (var pos = 0; pos < str.length; pos++) {
            crc ^= str.charCodeAt(pos);
            for (var i = 8; i !== 0; i--) {
                if ((crc & 0x0001) !== 0) {
                    crc >>= 1;
                    crc ^= 0xA001;
                } else
                    crc >>= 1;
            }
        }
        return crc;
    },
    _stringToBytes: function(str) {
        var ch, st, re = [];
        for (var i = 0; i < str.length; i++) {
            ch = str.charCodeAt(i); // get char
            st = []; // set up "stack"
            do {
                st.push(ch & 0xFF); // push byte to stack
                ch = ch >> 8; // shift value down by 1 byte
            }
            while (ch);
            // add stack contents to result
            // done because chars have "wrong" endianness
            re = re.concat(st.reverse());
        }
        // return an array of bytes
        return re;
    },
    _hexStringToString: function(inputstr) {
        var hex = inputstr.toString(); //force conversion
        var str = '';
        for (var i = 0; i < hex.length; i += 2)
            str += String.fromCharCode(parseInt(hex.substr(i, 2), 16));
        return str;
    },
    Calculate: function(str, inputType) {
        this.StringToCheck = str;
        if (this.CleanString(inputType)) {
            crcinputcrc16modbus=this.CRC16Modbus().toString(16).toUpperCase();
//  No reverse of bytes in AirTouch2+ docs, confirmed in data
//            crcinputcrc16modbus=crcinputcrc16modbus.substr(2) + crcinputcrc16modbus.substr(0, 2); //swap bytes
        }
    }
};

CRCMaster.init();

var inputType = "HEX";
var crcinputcrc16modbus;
var crcinput = msg.payload;

CRCMaster.Calculate(crcinput, inputType);

msg.payload = crcinput + crcinputcrc16modbus;

return msg;

function hexToBytes(hex) {
    for (var bytes = [], c = 0; c < hex.length; c += 2)
        bytes.push(parseInt(hex.substr(c, 2), 16));
    return bytes;
}

msg.payload = hexToBytes(msg.payload);

//node.warn(msg.payload);

msg.payload.unshift(0x55, 0x55);

msg.payload = Buffer.from(msg.payload);

return msg;

var bufACStatus = Buffer.from(
    [
//        0x55,0x55, // Header
        0x80,0xB0, // Address
        0x01,      // Message id
        0xC0,      // Message type
        0x00,0x08, // Data length
        0x23,0x00,0x00,0x00,0x00,0x00,0x00,0x00, // Data
//        0x7D,0xB0  // CRC MODBUS
    ]
)

function toHexString(byteArray) {
    return Array.from(byteArray, function (byte) {
        return ('0' + (byte & 0xFF).toString(16)).slice(-2);
    }).join('')
}

msg.payload = bufACStatus;
msg.payload = toHexString(msg.payload);

return msg;
